package javaCode_Daily;

public class LocalVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num1 = 20;
		int num2 = 10, num3 = 30, result;
		System.out.print("Number 1 value: " + num1);
		System.out.println("\t Number 2 value: " + num2 + "\t Number 3 value : " + num3);
		result = num1 + num2 + num3;
		System.out.println("The sum of all the numbers " + result);
	}

}
